var opskip = false;

function init_() {

	if($.cookie("opskip")=="skip") {
		opskip = true;
	}
	$.cookie("opskip", "skip",{ path: "/" }, { expires: 9999 })

	$("#cover").fadeOut(800);
	// if(winH<768&&sp_ua) {
	// 	TweenLite.to($("#logo svg"), 0, {y:20})
	// }
	in_resize()
		$(window).resize(function(event) {
		in_resize()
	});
	if(winH<768&&sp_ua){
		$('#index_cover, .top_fv, .top_slide_img').height(winH)
	}
	var $slider_top = $('.top_slide');
	$slider_top.slick({
		dots: true,
		autoplay:true,
		autoplaySpeed:4000,
		infinite: true,
		pauseOnHover:false,
		pauseOnDotsHover: false,
		draggable:false,
		pauseOnHover: false,
		speed: 2000,
		cssEase:'ease-in-out'//'linear'
	});

	$slider_top.on('init', function() {
	    var $dots = $slider_top.find('.slick-dots li');
	    $dots
	        .off('click')
	        .on('click', function(e){
	            var clickedIndex = $dots.index($(e.currentTarget));//クリックされたdotsのindex取得

	            $slider_top.slick('slickGoTo', clickedIndex);//本来の動作をslickGoToで代用
	        });
	})

	var $slider_container = $('.live_main'),
	    $slider = $('.top_live_slide');
		$slider.on('init', function(){
	  	$slider_container.addClass('initialized');
	});

	$slider.slick({
		dots: true,
		appendArrows: $slider_container,
		// prevArrow: '<div class="slider-arrow slider-prev fa fa-angle-left"></div>',
		// nextArrow: '<div class="slider-arrow slider-next fa fa-angle-right"></div>',
		slidesToShow: 2,
		slidesToScroll: 2,
		responsive: [{
			breakpoint: 768,
			settings: {
			slidesToShow: 1,
				slidesToScroll: 1
			}
	  	}]
	});
	/*
	// スライドの横幅を小数点以下pxまで表示
	$slider.on('setPosition', function(){
	  var slider_width = $slider.width(),
	      slide_gutter = $slider.find('.slick-slide').eq(0).css('margin-right').split('px')[0],
	      slides_num = $slider.slick('slickGetOption', 'slidesToShow'),
	      slide_width = (slider_width - slide_gutter * (slides_num - 1)) / slides_num;
	  $slider.find('.slick-active').css('width', slide_width + 'px');
	});
	*/
	if(winH<768&&sp_ua) {

		var $slider_container_n = $('#top_news'),
		    $slider_n = $('.news_main');

		// スライド初期化時にクラスを追加
		// はじめはdisplay:noneしておき、.initializedが追加されたらdisplay:block
		$slider_n.on('init', function(){
			$slider_container_n.addClass('initialized');
		});

		$slider_n.slick({
			appendArrows: $slider_container,
			prevArrow: '<div class=""></div>',
			nextArrow: '<div class=""></div>',
			slidesToShow: 2,
			slidesToScroll: 2
		});
		/*
		// スライドの横幅を小数点以下pxまで表示
		$slider_n.on('setPosition', function(){
		  var slider_width = $slider_n.width(),
		      slide_gutter = $slider_n.find('.slick-slide').eq(0).css('margin-right').split('px')[0],
		      slides_num = $slider_n.slick('slickGetOption', 'slidesToShow'),
		      slide_width = (slider_width - slide_gutter * (slides_num - 1)) / slides_num;
		  $slider_n.find('.slick-active').css('width', slide_width + 'px');
		});*/
	}

	$('.top_slide').slick('slickPause');

	if(!opskip)	{
		setTimeout(function() {
			$('.top_slide').slick('slickPlay');
			// $("#logo_country").shuffleLetters({
			// 	"text": $('.top_slide_img').find(".clock").eq(1).attr("data-taxt")
			// });
		},5500);
	} else {
		setTimeout(function() {
			$('.top_slide').slick('slickPlay');
			// $("#logo_country").shuffleLetters({
			// 	"text": $('.top_slide_img').find(".clock").eq(1).attr("data-taxt")
			// });
		},1500);
	}
	if(opskip) {
		TweenLite.to($("#logo svg #mark"),0, {opacity:0})
		TweenLite.to($("#logo svg #logo_text"),0, {opacity:0})
	}
	$(window).load(function() {
		if(!opskip) {
			TweenLite.to($('.top_slide_img img').eq(1),10,{delay:4.5,scale:1.2,ease:Power0.easeNone})
			TweenLite.to($('.top_slide_img img').eq(0),10,{delay:4.5,scale:1.2,ease:Power0.easeNone})
			TweenLite.to($('header h1 img, #gheader'),1.5,{delay:4.5,opacity:1})
			// TweenLite.to($("#logo svg"), 2, {delay:1.7,y:0})
			// TweenLite.to($("#logo svg #mark"), 2, {delay:1.7,scale:2,opacity:0.2,y:15, transformOrigin:'50% 50%', ease: Power2.easeInOut})
			// TweenLite.to($("#logo svg #logo_text"), 1, {delay:1.7,opacity:0})
			TweenLite.to($("#copy"),2, {delay:2.3,opacity:1})
			// TweenLite.to($("#logo_copy"),2, {delay:2.3,opacity:1})
			// $("#logo_typo svg path").each(function(index, el) {
			//   TweenLite.to($(this),1+ index*0.1, {delay:1.7+index*0.1,opacity:1})
			// });

			$("#index_cover").delay(4000).fadeOut(2000)
			TweenLite.to($("#copy"),1.2, {delay:4,opacity:0})
			TweenLite.to($(".st0"), 1.2, {delay:4,fill:"#FFFFFF"})
			// TweenLite.to($("#logo_typo"),1.2, {delay:4,opacity:0})
			// TweenLite.to($("#logo_copy"),1.2, {delay:4,color:"#FFFFFF"})

		} else {

			TweenLite.to($('.top_slide_img img').eq(1),0,{delay:0.5,scale:1.2,ease:Power0.easeNone})
			TweenLite.to($('.top_slide_img img').eq(0),0,{delay:0.5,scale:1.2,ease:Power0.easeNone})
			TweenLite.to($('header h1 img,#gheader'),0,{delay:0.5,opacity:1})
			// TweenLite.to($("#logo svg"), 0, {y:0})
			// TweenLite.to($("#logo svg #mark"),0, {scale:2,opacity:0.2,y:15, transformOrigin:'50% 50%', ease: Power2.easeInOut})
			// TweenLite.to($("#logo svg #logo_text"),0, {opacity:0})
			TweenLite.to($("#copy"),1, {opacity:1})
			// TweenLite.to($("#logo_copy"),1, {opacity:1})

			$("#index_cover").delay(300).fadeOut(1000)
			TweenLite.to($("#copy"),1.2, {opacity:0})
			TweenLite.to($(".st0"), 0, {fill:"#FFFFFF"})
			// TweenLite.to($("#logo_typo"),0, {opacity:0})
			// TweenLite.to($("#logo_copy"),0, {color:"#FFFFFF"})
		}

		$('.top_slide').on('beforeChange',function(event, slick, currentSlide, nextSlide) {

			TweenLite.to($('.top_slide_img img:not(:eq('+(nextSlide+1)+'))'),0,{delay:2,scale:1,ease:Power0.easeNone})
			// var text_next = $('.top_slide_img').find(".clock").eq(nextSlide+1).attr("data-taxt")
			// $("#logo_country").shuffleLetters({
			// 	"step"  : 40-text_next.length,
			// 	"fps"   : 20,
			// 	"text": text_next
			// });
			var winWWW = winW/3.6
			TweenLite.to($('.top_slide_img img').eq(nextSlide),2,{x:winWWW,ease:Power1.easeInOut})
			TweenLite.to($('.top_slide_img img').eq(nextSlide+1),0,{x:-winWWW})
			TweenLite.to($('.top_slide_img img').eq(nextSlide+1),2,{x:0,ease:Power1.easeInOut})
			TweenLite.to($('.top_slide_img img').eq(nextSlide+1),4,{delay:2,scale:1.1,ease:Power0.easeNone})
			if(nextSlide==0){
				//TweenLite.to($('.top_slide_img img').eq(1),10,{scale:1.2,ease:Power0.easeNone})
				TweenLite.to($('.top_slide_img img').eq($('.top_slide_img img').length-1),0,{x:-winWWW})
				TweenLite.to($('.top_slide_img img').eq($('.top_slide_img img').length-1),2,{x:0,ease:Power1.easeInOut})
				TweenLite.to($('.top_slide_img img').eq($('.top_slide_img img').length-1),10,{delay:2,scale:1.1,ease:Power0.easeNone})
			}
		/**/
			/*TweenLite.to($(".clock,.line_r,.line_l"),1,{x:-100,ease:Power3.easeIn})
			TweenLite.to($(".clock,.line_r,.line_l"),1,{delay:1,x:0,ease:Power3.easeIn})*/
		})

		if($("#top_live li").length==0) {
			$("#top_live").remove()

		}
	})
}


